// You are using Java
import java.util.Scanner;
interface Principal {
    public double getPrincipal();
}
interface InterestRate {
    public double getInterestRate();
}
class Loan implements Principal, InterestRate {
    private double principal;
    private double interestRate;
    private int years;
    public Loan(double principal, double interestRate, int years) {
        this.principal = principal;
        this.interestRate = interestRate;
        this.years = years;
    }
    public double getPrincipal() {
        return principal > 0 ? principal : 0;
    }
    public double getInterestRate() {
        return interestRate > 0 ? interestRate : 0;
    }
    public void displayInterest() {
        if (getPrincipal() != 0 && getInterestRate() != 0) {
            System.out.printf("Total interest paid: Rs.%.2f\n", principal * interestRate * years);
        } else {
            System.out.println("Invalid input values!");
        }
    }
}
class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        double principal = sc.nextDouble();
        double interestRate = sc.nextDouble();
        int years = sc.nextInt();
        Loan l = new Loan(principal, interestRate, years);
        l.displayInterest();
    }
}

// You are using Java
import java.util.Scanner;
interface StepCounter {
    public int countSteps();
}
interface CalorieCounter {
    public double caloriesBurned();
}
class FitnessTracker implements StepCounter, CalorieCounter {
    int days;
    int[] Steps;
    public FitnessTracker(int days, int[] Steps) {
        this.days = days;
        this.Steps = Steps;
    }
    public int countSteps() {
        int steps = 0;
        for (int step : Steps) {
            steps += step;
        }
        return steps;
    }
    public double caloriesBurned() {
        return ((double) (countSteps() / 100.0) * 20);
    }
}
class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int days = sc.nextInt();
        int[] Steps = new int[days];
        for (int i = 0; i < days; i++) {
            Steps[i] = sc.nextInt();
        }
        FitnessTracker ft = new FitnessTracker(days, Steps);
        System.out.println("Total Steps: " + ft.countSteps());
        System.out.printf("Calories Burned: %.2f\n", ft.caloriesBurned());
    }
}

// You are using Java
import java.util.Scanner;
interface NumberInput {
    public int getNumber();
}
interface AutomorphicChecker {
    public boolean checkAutomorph();
}
class AutomorphicNumber implements NumberInput, AutomorphicChecker {
    int n;
    public AutomorphicNumber(int n) {
        this.n = n;
    }
    public int getNumber() {
        return n;
    }
    public boolean checkAutomorph() {
        int count = 0;
        int temp = n;
        while (temp > 0) {
            count++;
            temp /= 10;
        }
        int sq = n * n;
        int sqend = sq % ((int) Math.pow(10, count));
        return sqend == n;
    }
}
class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        AutomorphicNumber an = new AutomorphicNumber(n);
        if (an.checkAutomorph()) {
            System.out.println(an.getNumber() + " is an automorphic number");
        } else {
            System.out.println(an.getNumber() + " is not an automorphic number");
        }
    }
}

// You are using Java
import java.util.Scanner;
interface ExpenseRecorder {
    public void recordExpenses();
}
interface BudgetCalculator {
    public void calculateBudget();
}
class ExpenseTracker implements ExpenseRecorder, BudgetCalculator {
    double budget;
    double[] Expenses = new double[100];
    public void recordExpenses() {
        Scanner sc = new Scanner(System.in);
        budget = sc.nextDouble();
        int i = 0;
        while (true) {
            double input = sc.nextDouble();
            if (input == 0.0) {
                break;
            }
            Expenses[i] = input;
            i++;
        }
    }
    public void calculateBudget() {
        recordExpenses();
        for (int i = 0; i < Expenses.length; i++) {
            budget -= Expenses[i];
        }
        if (budget > 0) {
            System.out.printf("Remaining budget:  Rs. %.2f\n", budget);
        } else {
            System.out.println("No remaining budget, You've exceeded your budget!");
        }
    }
}
class Main {
    public static void main(String[] args) {
        ExpenseTracker et = new ExpenseTracker();
        et.calculateBudget();
    }
}

// You are using Java
import java.util.Scanner;
interface ShapeInput {
    public void inputDimensions(double b1, double b2, double h, double s1, double s2);
}
interface ShapeCalculator {
    public double getArea();
    public double getPerimeter();
}
class Trapezoid implements ShapeInput {
    double b1, b2, h, s1, s2;
    public void inputDimensions(double b1, double b2, double h, double s1, double s2) {
        this.b1 = b1;
        this.b2 = b2;
        this.h = h;
        this.s1 = s1;
        this.s2 = s2;
    }
    public double getArea() {
        return 0.5 * (b1 + b2) * h;
    }
    public double getPerimeter() {
        return b1 + b2 + s1 + s2;
    }
}
class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        double b1 = sc.nextDouble();
        double b2 = sc.nextDouble();
        double h = sc.nextDouble();
        double s1 = sc.nextDouble();
        double s2 = sc.nextDouble();
        Trapezoid t = new Trapezoid();
        t.inputDimensions(b1, b2, h, s1, s2);
        System.out.printf("Area of the Trapezoid: %.2f\n", t.getArea());
        System.out.printf("Perimeter of the Trapezoid: %.2f\n", t.getPerimeter());
    }
}
