import java.util.Scanner;
public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int temp = n;
        int rem = 0;
        int count = 0;
        int prod = 1;
        while (temp > 0) {
            rem = temp % 10;
            if (rem % 2 != 0) {
                prod *= rem;
                count += 1;
            }
            temp /= 10;
        }
        if (count > 0) {
            System.out.println(prod);
        } else {
            System.out.println("No odd digits found");
        }
        scanner.close();
    }
}

import java.util.Scanner;
class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        double h=scanner.nextDouble();
        double w=scanner.nextDouble();
        double bmi=w/(h*h);
        System.out.printf("BMI: %.2f\n", bmi);
        if (bmi<=18.5)
        {
            System.out.println("Classification: Underweight");
        }
        else if((bmi>=18.6)&&(bmi<=24.9))
        {
            System.out.println("Classification: Normal Weight");
        }
        else if((bmi>=25)&&(bmi<=29.9))
        {
            System.out.println("Classification: Overweight");
        }
        else
        {
            System.out.println("Classification: Obese");
        }
        scanner.close();
    }
}
