import java.util.Scanner;
public class Main{
    public static void main(String[] args){
        Scanner sc=new Scanner(System.in);
        int n=sc.nextInt();
        int[] scores=new int[n];
        for(int i=0;i<n;i++){
            scores[i]=sc.nextInt();
        }
        int k=sc.nextInt();
        for (int i=0;i<k;i++){
            int maxIndex=i;
            for(int j=i+1;j<n;j++){
                if(scores[j]>scores[maxIndex]){
                    maxIndex=j;
                }
            }
            int temp=scores[i];
            scores[i]=scores[maxIndex];
            scores[maxIndex]=temp;
        }
        System.out.println(scores[k-1]);
        sc.close();
    }
}

