import java.util.Scanner;

class Main {

    // Function to count vowels in a string and check if vowels are present
    public static void countAndCheckVowels(String str) {
        int vowelCount = 0;
        boolean vowelsPresent = false;
        // Convert the string to lowercase to handle both lowercase and uppercase vowels
        str = str.toLowerCase();

        for (int i = 0; i < str.length(); i++) {
            char ch = str.charAt(i);
            if (ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u') {
                vowelCount++;
                vowelsPresent = true;
            }
        }

        System.out.println("Number of vowels: " + vowelCount);
        if (vowelsPresent) {
            System.out.println("Vowels are present.");
        } else {
            System.out.println("Vowels are not present.");
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String input = scanner.nextLine().trim();
        scanner.close();

        countAndCheckVowels(input);
    }
}

