import java.util.Scanner;
class Main
{
    public static void main(String[] args)
    {
        Scanner sc=new Scanner(System.in);
        String text=sc.nextLine();
        int count=0;
        for(int i=0;i<text.length();i++)
        {
            switch(text.charAt(i))
            {
                case 'a':
                    count++;
                    break;
                case 'e':
                    count++;
                    break;
                case 'i':
                    count++;
                    break;
                case 'o':
                    count++;
