                case 'I':
                    count++;
                    break;
                case 'O':
                    count++;
                    break;
                case 'U':
                    count++;
                    break;
            }
        }
        System.out.println("Number of vowels: "+count);
        if(count>0)
        {
            System.out.println("Vowels are present.");
        }
        else
        {
            System.out.println("Vowels are not present.");
        }
    }
}
