import java.util.Scanner;
class BankAcc
{
    double balance;
    String accType;
    public BankAcc(double balance,String accType)
    {
        this.balance=balance;
        this.accType=accType;
    }
    public double AnnInterest()
    {
        double annInt=0;
        if(accType.equals("Savings"))
        {
            annInt = 0.05*balance;
        }
        else if(accType.equals("Checking"))
        {
            annInt = 0.02*balance;
        }
        else
        {
            annInt = 0.01*balance;
        }
        return annInt;
    }
}
