import java.util.Scanner;
class AgeCalculatorFunctions
{
    int byear;
    int cyear;
    public AgeCalculatorFunctions(int byear,int cyear)
    {
        this.byear=byear;
        this.cyear=cyear;
    }
    public void calcLeap()
    {
        int count=0;
        for(int i=byear;i<=cyear;i++)
        {
            if((i%4==0)&&((i%100!=0)||(i%400==0)))
            {
                count++;
            }
        }
        System.out.println(count);
    }
}
class Main
{
    public static void main(String[] args)
    {
        Scanner sc=new Scanner(System.in);
        int byear=sc.nextInt();
        int cyear=sc.nextInt();
        AgeCalculatorFunctions ageCal=new AgeCalculatorFunctions(byear,cyear);
        ageCal.calcLeap();
    }
}

