import java.util.Scanner;
class SecondsToTime {
    private int hours;
    private int minutes;
    private int seconds;
    public SecondsToTime(int totalSeconds) {
        if (totalSeconds < 0) {
            return;
        }
        this.hours = totalSeconds / 3600;
        totalSeconds %= 3600;
        this.minutes = totalSeconds / 60;
        this.seconds = totalSeconds % 60;
    }
    public int getHours() { return hours; }
    public int getMinutes() { return minutes; }
    public int getSeconds() { return seconds; }
    public void setHours(int hours) {
        if (hours >= 0) { this.hours = hours; }
    }
    public void setMinutes(int minutes) {
        if (minutes >= 0) { this.minutes = minutes; }
    }
    public void setSeconds(int seconds) {
        if (seconds >= 0) { this.seconds = seconds; }
    }
    public void displayTime() {
        System.out.printf("%02d:%02d:%02d", getHours(), getMinutes(), getSeconds());
    }
}
class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int totalSeconds = scanner.nextInt();
        scanner.close();
        SecondsToTime timeConverter = new SecondsToTime(totalSeconds);
        timeConverter.displayTime();
    }
}

