import java.util.Scanner;
class GPACalc
{
    double[] arr;
    int num;
    double GPA;
    public GPACalc(double[] arr)
    {
        this.arr=arr;
    }
    public void dispGPA()
    {
        double sum=0;
        this.num=arr.length;
        for (int i=0;i<num;i++)
        {
            sum+=arr[i];
        }
        this.GPA=sum/num;
        System.out.printf("GPA: %.2f\n",GPA);
    }
    public void dispDetails()
    {
        if(GPA>=3.5&&num>=5)
        {
            System.out.println("Eligible for a scholarship");
        }
        else
        {
            System.out.println("Not eligible for a scholarship");
        }
    }
}
class Main
{
    public static void main(String[] args)
    {
        Scanner sc=new Scanner(System.in);
        int n=sc.nextInt();
        double[] array=new double[n];
        for(int i=0;i<n;i++)
        {
            array[i]=sc.nextDouble();
        }
        GPACalc gpac=new GPACalc(array);
        gpac.dispGPA();
        gpac.dispDetails();
    }
}
