import java.util.Scanner;
public class Main {
    static class CustomClass {
        //type your code here
        public void displayMessage(String s)
        {
            System.out.println("CustomClass: Hey, "+s+"! Nice to meet you");
        }
        public boolean instOfStr(String s)
        {
            return s instanceof String;
        }
        public boolean instOfClass(CustomClass obj)
        {
            return obj instanceof CustomClass;
        }
        public void sqrtNum(int n)
        {
            System.out.println("Square root of the entered number: "+Math.sqrt(n));
        }
    }
    public static void main(String[] args) {
        //type your code here
        Scanner sc=new Scanner(System.in);
        String s=sc.nextLine();
        int n=sc.nextInt();
        CustomClass customObj=new CustomClass();
        System.out.println("Name is an instance of String: "+customObj.instOfStr(s));
        customObj.sqrtNum(n);
        customObj.displayMessage(s);
        System.out.println("customObj is an instance of CustomClass: "+customObj.instOfClass(customObj));
    }
}
