import java.util.Scanner;
class Event
{
    protected String name;
    protected String detail;
    protected String ownerName;
    public Event(String name, String detail, String ownerName)
    {
        this.name=name;
        this.detail=detail;
        this.ownerName=ownerName;
    }
    public double projectedRevenue()
    {
        return 0.0;
    }
}
class Exhibition extends Event
{
    int noOfStall;
    public Exhibition(String name, String detail, String ownerName, int noOfStall)
    {
        super(name, detail, ownerName);
        this.noOfStall=noOfStall;
    }
    public double projectedRevenue()
    {
        return noOfStall*10000;
    }
}
class StageEvent extends Event
{
    int noOfShows;
    int noOfSeatsPerShow;
    public StageEvent(String name, String detail, String ownerName, int noOfShows, int noOfSeatsPerShow)
    {
        super(name, detail, ownerName);
        this.noOfShows=noOfShows;
        this.noOfSeatsPerShow=noOfSeatsPerShow;
    }
    public double projectedRevenue()
    {
        return noOfShows*noOfSeatsPerShow*50;
    }
}
class Main
{
    public static void main(String [] args)
    {
        Scanner sc=new Scanner(System.in);
        String name=sc.nextLine();
        String detail=sc.nextLine();
        String ownerName=sc.nextLine();
        int choice=sc.nextInt();
        if(choice==1)
        {
            int noOfStall=sc.nextInt();
            Event e=new Exhibition(name, detail, ownerName, noOfStall);
            System.out.println(e.projectedRevenue());
        }
        else if(choice==2)
        {
            int noOfShows=sc.nextInt();
            int noOfSeatsPerShow=sc.nextInt();
            Event se=new StageEvent(name, detail, ownerName, noOfShows, noOfSeatsPerShow);
            System.out.println(se.projectedRevenue());
        }
    }
}

