import java.util.Scanner;
class Aircraft
{
    String aircraftName;
    String source;
    String destination;
    public Aircraft(String aircraftName, String source, String destination)
    {
        this.aircraftName=aircraftName;
        this.source=source;
        this.destination=destination;
    }
    public void display()
    {
        System.out.println("Aircraft Name : "+aircraftName);
        System.out.println("Source : "+source);
        System.out.println("Destination : "+destination);
    }
}
class PublicAircraft extends Aircraft
{
    int noOfKgsallowed;
    float addFeeperKg;
    public PublicAircraft(String aircraftName, String source, String destination, int noOfKgsallowed, float addFeeperKg)
    {
        super(aircraftName, source, destination);
        this.noOfKgsallowed=noOfKgsallowed;
        this.addFeeperKg=addFeeperKg;
    }
    public void display()
    {
        System.out.println("Aircraft Type : Public Aircraft");
        super.display();
        System.out.println("Check in before two hours : Yes");
        System.out.println("Number of kgs allowed per person : "+noOfKgsallowed);
        System.out.println("Additional fee charged for extra baggage per Kg : "+addFeeperKg);
    }
}
class PrivateAirCraft extends Aircraft
{
    String pilotPrefer;
    String purpose;
    public PrivateAirCraft(String aircraftName, String source, String destination, String pilotPrefer, String purpose)
    {
        super(aircraftName, source, destination);
        this.pilotPrefer=pilotPrefer;
        this.purpose=purpose;
    }
    public void display()
    {
        System.out.println("Aircraft Type : Private Aircraft");
        super.display();
        System.out.println("Check in before two hours : No");
        System.out.println("Pilot Name : "+pilotPrefer);
        System.out.println("Purpose : "+purpose);
    }
}
class Main
{
    public static void main(String[] args)
    {
        Scanner sc=new Scanner(System.in);
        String aircraftName=sc.nextLine();
        String source=sc.nextLine();
        String destination=sc.nextLine();
        int flightType=Integer.parseInt(sc.nextLine());
        if(flightType==1)
        {
            int noOfKgsallowed=Integer.parseInt(sc.nextLine());
            float addFeeperKg=Float.parseFloat(sc.nextLine());
            PublicAircraft pub=new PublicAircraft(aircraftName,source,destination,noOfKgsallowed,addFeeperKg);
            pub.display();
        }
        else if(flightType==2)
        {
            String pilotPrefer=sc.nextLine();
            String purpose=sc.nextLine();
            PrivateAirCraft priv=new PrivateAirCraft(aircraftName,source,destination,pilotPrefer,purpose);
            priv.display();
        }
    }
}

import java.util.Scanner;
class BasicCustomer {
    private String name;
    private String city;
    private int age;
    private String gender;
    private int billamount;

    BasicCustomer() {
        this.name = "";
        this.city = "";
        this.age = 0;
        this.gender = "";
        this.billamount = 0;
    }

    BasicCustomer(String n, String c, int age, String g, int amt) {
        this.name = n;
        this.city = c;
        this.age = age;
        this.gender = g;
        this.billamount = amt;
    }

    public void setName(String n) { this.name = n; }
    public void setCity(String c) { this.city = c; }
    public void setAge(int a) { this.age = a; }
    public void setGender(String g) { this.gender = g; }
    public void setBillAmount(int b) { this.billamount = b; }

    public String getName() { return name; }
    public String getCity() { return city; }
    public int getAge() { return age; }
    public String getGender() { return gender; }
    public int getBillAmount() { return billamount; }

    public void calculateDiscount() {
        int bill = getBillAmount();
        int discount;
        if (bill >= 1 && bill <= 4999) discount = 100;
        else if (bill <= 7499) discount = 250;
        else if (bill <= 9999) discount = 500;
        else if (bill <= 19999) discount = 750;
        else if (bill <= 29999) discount = 1000;
        else discount = 1500;
        System.out.println("Discount: " + discount);
    }
}

class PremiumCustomer extends BasicCustomer {
    private int subamt;

    PremiumCustomer() {
        super();
        this.subamt = 0;
    }

    PremiumCustomer(String name, String city, int age, String gen, int b, int sub) {
        super(name, city, age, gen, b);
        this.subamt = sub;
    }

    public void setSubamt(int s) { this.subamt = s; }
    public int getSubamt() { return subamt; }

    @Override
    public void calculateDiscount() {
        int bill = getBillAmount();
        int discount;
        if (bill >= 1 && bill <= 4999) discount = 200;
        else if (bill <= 7499) discount = 400;
        else if (bill <= 9999) discount = 700;
        else if (bill <= 19999) discount = 1000;
        else if (bill <= 29999) discount = 1500;
        else discount = 2000;
        System.out.println("Discount: " + discount);
    }
}

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String name = sc.nextLine();
        String city = sc.nextLine();
        int age = Integer.parseInt(sc.nextLine());
        String gender = sc.nextLine();
        int billamount = Integer.parseInt(sc.nextLine());
        int choice = Integer.parseInt(sc.nextLine());

        BasicCustomer customer = new BasicCustomer(name, city, age, gender, billamount);

        System.out.println("Name: " + customer.getName());
        System.out.println("City: " + customer.getCity());
        System.out.println("Age: " + customer.getAge());
        System.out.println("Gender: " + customer.getGender());
        System.out.println("Total Bill Amount: " + customer.getBillAmount());

        if (choice == 0) {
            customer.calculateDiscount();
        } else if (choice == 1) {
            int subamt = Integer.parseInt(sc.nextLine());
            PremiumCustomer premiumCustomer = new PremiumCustomer(name, city, age, gender, billamount, subamt);
            System.out.println("Subscription fee/month: " + premiumCustomer.getSubamt());
            premiumCustomer.calculateDiscount();
        }
        sc.close();
    }
}

// You are using Java
import java.util.Scanner;
class Shape {
    String type;
    public Shape(String type) {
        this.type = type;
    }
    public void Area() {
        // To be overridden
    }
}
class Rectangle extends Shape {
    int length, width;
    public Rectangle(int length, int width) {
        super("Rectangle");
        this.length = length;
        this.width = width;
        System.out.println("Creating a rectangle with length " + length + " and width " + width + ".");
    }
    public void Area() {
        System.out.printf("Area of rectangle: %.2f\n", (float) length * width);
    }
}
class Circle extends Shape {
    int radius;
    public Circle(int radius) {
        super("Circle");
        this.radius = radius;
        System.out.println("Creating a circle with radius " + radius + ".");
    }
    public void Area() {
        System.out.printf("Area of circle: %.2f\n", (Math.PI * radius * radius));
    }
}
class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int length = sc.nextInt();
        int width = sc.nextInt();
        int radius = sc.nextInt();
        Shape r = new Rectangle(length, width);
        Shape c = new Circle(radius);
        r.Area();
        c.Area();
    }
}
